import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import { useAppContext } from '../context/AppContext';

const ReportPage = () => {
  const { fetchReport } = useAppContext();
  const [reportData, setReportData] = useState(null);

  useEffect(() => {
    const fetch = async () => {
      const response = await fetchReport();
      setReportData(response.data);
    }
    fetch();
  });

  console.log("reportData na pagina ReportPage", reportData)
  return (
    <div className="container mt-4">
      <h2 className="mb-4">Relatório Consolidado</h2>

      <Table striped bordered hover responsive>
        <thead className="table-dark">
          <tr>
            <th>Código</th>
            <th>Equipamento</th>
            <th>Data Fabricação</th>
            <th>Descrição</th>
            <th>Data Manutençao</th>
          </tr>
        </thead>
        <tbody>
          {reportData.map((item, index) => (
            <tr key={index}>
              <td>{item.equipment_code || item[0]}</td>
              <td>{item.formatted_maintenance_date || item[4]}</td>
              <td>{item.equipment_name || item[1]}</td>
              <td>{item.maintenance_description || item[2]}</td>
              <td>{item.formatted_manufacture_date || item[6]}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default ReportPage;



