import React from 'react';
import { Table } from 'react-bootstrap';
import { useAppContext } from '../context/AppContext';

const ReportTable = () => {
  const { reportData } = useAppContext();

  return (
    <Table striped bordered hover responsive>
      <thead className="table-dark">
        <tr>
          <th>Código</th>
          <th>Equipamento</th>
          <th>Descrição</th>
          <th>Data</th>
          <th>Data Fabricação</th>
        </tr>
      </thead>
      <tbody>
        {reportData.map((item, index) => (
          <tr key={index}>
            <td>{item.equipment_code || item[0]}</td>
            <td>{item.equipment_name || item[1]}</td>
            <td>{item.maintenance_description || item[2]}</td>
            <td>{item.formatted_maintenance_date || item[4]}</td>
            <td>{item.formatted_manufacture_date || item[6]}</td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default ReportTable;
